void __onWidgetResize(GtkWidget* widget, GdkRectangle* allocation, gpointer param) {
    gtk_widget_set_size_request((GtkWidget*)param, allocation->width, allocation->height);
}

struct __OpenGLImpl {
    GtkGLArea* glArea;
    bool withView;
};

void OpenGL::_implInit(boom::OpenGLOptions const& options) {
    if (options.view.has_value()) {
        auto const area = GTK_GL_AREA(gtk_gl_area_new());
        auto const view = options.view.value();
        auto const parent = GTK_FIXED(view->_impl->widget);
        auto const child = GTK_WIDGET(area);
        auto const width = gtk_widget_get_width(view->_impl->widget);
        auto const height = gtk_widget_get_height(view->_impl->widget);
        gtk_fixed_put(parent, child, 0, 0);
        gtk_widget_set_size_request(child, width, height);
        g_signal_connect(parent, "size-allocate", G_CALLBACK(boom::__onWidgetResize), child);
        _impl = new boom::__OpenGLImpl{
            .glArea = area,
            .withView = true
        };
    } else {
        _impl = new boom::__OpenGLImpl{
            .glArea = GTK_GL_AREA(gtk_gl_area_new()),
            .withView = false
        };
    }
    gtk_widget_set_visible(GTK_WIDGET(_impl->glArea), false);
}

void OpenGL::_implDone() {
    if (_impl->withView) {
        auto const child = GTK_WIDGET(_impl->glArea);
        auto const parent = gtk_widget_get_parent(child);
        if (parent != nullptr) {
            gtk_fixed_remove(GTK_FIXED(parent), child);
            g_signal_handlers_disconnect_by_func(parent, (gpointer)(boom::__onWidgetResize), child);
        }
    }
    delete _impl;
}

void OpenGL::_implSwap() const {
    gtk_gl_area_queue_render(_impl->glArea);
}

void OpenGL::_implCurrent() const {
    gtk_gl_area_make_current(_impl->glArea);
    if (gtk_gl_area_get_error(_impl->glArea) != nullptr) {
        boom::Abort("ERROR: boom::OpenGL::_implCurrent failed: gtk_gl_area_make_current() failed");
    }
}

